mapper.update:
UPDATE trunk 
	SET 
		MODIFYTIME = NOW()
			, TELECOM_NAME 			= #{telecomName} 
				, TRUNK_NAME 			= #{trunkName} 
				, TRUNK_IP 				= #{trunkIp} 
				, TRUNK_PORT 			= #{trunkPort} 
			, TRUNK_USERNAME 		= #{trunkUsername} 
			, TRUNK_PASSWORD 		= #{trunkPassword} 
				, STATUS 				= #{status} 
	WHERE	SEQ = #{seq}
